INSERT INTO `testimony` (`id`, `name`, `commentary`, `note`, `updated_at`, `is_validated`) VALUES
(2, 'Alexis PERSONA', 'Je suis venu faire posé un jeu de pneus neufs pour mon départ en vacances. Cela a été fait très rapidement, et le prix est plus que raisonnable. Je suis très satisfait.', 4, '2023-07-13 17:36:14', 1),
(3, 'Jean PROTOTYPE', 'J\'ai refais ma carte grise après avoir acheté un nouveau véhicule. Je suis venu un samedi donc l\'attente à été un peu longue mais les vendeurs ont été très agréables.', 3, '2023-07-13 18:07:16', 1),
(4, 'Gustave CONTENT', 'Je suis venu changer ma batterie en rentrant de voyage après le nouvel an. Les vendeurs m\'ont accueilli avec un énorme sourire et m\'ont même repris ma batterie avec un tarif préférentiel pour fêter la nouvelle année. FONCEZ !', 5, '2023-07-13 18:07:50', 1),
(14, 'Kevin PASATISFAIT', 'Je suis venu pour faire disparaitre une rayure sur ma carrosserie. J\'ai acheté un produit qui permet de camoufler les rayures de la carrosserie mais je vois encore une légère marque. La rayure n\'a pas entièrement disparu.', 2, '2023-07-14 11:40:01', 1),
(15, 'Hubert ANONYME', 'Je suis venu installé des pneus 4 saisons, et aussi acheté des chaînes en prévision de l\'hiver. La pose a été très rapide, je n\'es pratiquement pas attendu. Et les prix défient toute concurrence.', 5, '2023-07-14 11:45:10', 1),
(18, 'AVIS DE TEST', '/!\\ A NE PAS VALIDER /!\\ ceci est un avis de test pour montrer le fonctionnement du panel des témoignages et de la section infos du tableau de bord.', 5, '2023-07-14 12:51:09', 0);

